import React, { useState, useEffect, useCallback } from "react";
import Papa from "papaparse";
import GrapherWrapper from "./GrapherWrapper";
import { SVD } from "svd-js";
import "./App.css";

const MAX_DATA_POINTS = 5000; // Adjust based on your system's memory
const NUM_FACTORS = 20;

const loadCSVData = (csvFilePath) => {
  return new Promise((resolve, reject) => {
    Papa.parse(csvFilePath, {
      download: true,
      dynamicTyping: true,
      complete: (result) => {
        // resolve(result.data.slice(1, MAX_DATA_POINTS)); // Skip header
        resolve(result.data)
      },
      error: (error) => reject(error),
    });
  });
};

function App() {
  const [userId, setUserId] = useState("");
  const [levels, setLevels] = useState(1);
  const [graphData, setGraphData] = useState({ nodes: [], edges: [] });
  const [selectedNode, setSelectedNode] = useState(null);
  const [model, setModel] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);

  useEffect(() => {
    const initializeModel = async () => {
      setIsProcessing(true);
      try {
        const data = await loadCSVData("sample_user_ratings.csv");
        const processed = processRatingsData(data);
        setModel(processed);
      } catch (error) {
        console.error("Error initializing model:", error);
      }
      setIsProcessing(false);
    };
    initializeModel();
  }, []);

  const processRatingsData = (data) => {
    // Parse CSV data
    console.log("Raw Data:", data);
    const ratings = data.map(row => ({
      user: row[2].toString(),
      item: row[1].toString(),
      rating: parseFloat(row[0])
    }));

    console.log("Parsed Ratings:", ratings);

    // Create mappings
    const users = [...new Set(ratings.map(r => r.user))];
    const items = [...new Set(ratings.map(r => r.item))];
    const userMap = new Map(users.map((u, i) => [u, i]));
    const itemMap = new Map(items.map((i, idx) => [i, idx]));

    // Calculate global mean
    const globalMean = ratings.reduce((sum, r) => sum + r.rating, 0) / ratings.length;

    // Calculate biases
    const userBias = calculateBias(ratings, "user", globalMean, userMap);
    const itemBias = calculateBias(ratings, "item", globalMean, itemMap);

    // Create COO matrix
    const rows = [];
    const cols = [];
    const values = [];

    ratings.forEach(r => {
      const residual = r.rating - globalMean - userBias.get(r.user) - itemBias.get(r.item);
      rows.push(userMap.get(r.user));
      cols.push(itemMap.get(r.item));
      values.push(residual);
    });

    // Convert COO to dense matrix
    const denseMatrix = cooToDense(rows, cols, values, users.length, items.length);

    // Perform SVD
    const { u, v, q } = SVD(denseMatrix, NUM_FACTORS);
    const userFactors = u;
    const itemFactors = v.transpose();

    return {
      users,
      items,
      userMap,
      itemMap,
      globalMean,
      userBias,
      itemBias,
      userFactors,
      itemFactors,
      ratings
    };
  };

  const cooToDense = (rows, cols, values, nRows, nCols) => {
    const matrix = Array.from({ length: nRows }, () => new Array(nCols).fill(0));
    for (let i = 0; i < rows.length; i++) {
      matrix[rows[i]][cols[i]] = values[i];
    }
    return matrix;
  };

  const calculateBias = (ratings, entityType, globalMean, entityMap) => {
    const biasMap = new Map();
    const sumMap = new Map();
    const countMap = new Map();

    ratings.forEach(r => {
      const entity = entityType === "user" ? r.user : r.item;
      const residual = r.rating - globalMean - 
        (entityType === "user" ? 0 : model?.userBias.get(r.user) || 0);

      sumMap.set(entity, (sumMap.get(entity) || 0) + residual);
      countMap.set(entity, (countMap.get(entity) || 0) + 1);
    });

    sumMap.forEach((sum, entity) => {
      biasMap.set(entity, sum / countMap.get(entity));
    });

    return biasMap;
  };

  const getRecommendations = (userId) => {
    if (!model || !model.userMap.has(userId)) return [];

    const userIdx = model.userMap.get(userId);
    const userFactor = model.userFactors[userIdx];
    const userB = model.userBias.get(userId) || 0;

    return model.items
      .filter(item => !model.ratings.some(r => r.user === userId && r.item === item))
      .map(item => {
        const itemIdx = model.itemMap.get(item);
        const itemB = model.itemBias.get(item) || 0;
        const itemFactor = model.itemFactors[itemIdx];
        const score = model.globalMean + userB + itemB + 
          dotProduct(userFactor, itemFactor);
        return { item, score };
      })
      .sort((a, b) => b.score - a.score)
      .slice(0, 10);
  };

  const dotProduct = (a, b) => a.reduce((sum, val, i) => sum + val * b[i], 0);

  const generateGraphData = (recommendations) => {
    const nodes = [{ id: `user-${userId}`, label: `User ${userId}`, level: 0 }];
    const edges = [];

    recommendations.forEach(({ item }) => {
      nodes.push({ id: item, label: `${item} (Recommended)`, level: 1 });
      edges.push({ source: `user-${userId}`, target: item });
    });

    return { nodes, edges };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!userId || !model) return;

    const recommendations = getRecommendations(userId);
    setGraphData(generateGraphData(recommendations));
    setSelectedNode(null);
  };

  const handleNodeClick = useCallback((nodeId) => {
    setSelectedNode(nodeId);
  }, []);

  return (
    <div className="app-container">
      <h1>Recommendation Graph</h1>
      {isProcessing && <div className="loading">Processing data...</div>}

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          placeholder="Enter User ID"
          required
        />
        <button type="submit">Generate</button>
      </form>

      <GrapherWrapper
        nodes={graphData.nodes}
        edges={graphData.edges}
        onNodeClick={handleNodeClick}
      />

      {selectedNode && (
        <div className="details">
          <h3>Node: {selectedNode}</h3>
          {selectedNode.startsWith('user-') ? (
            <p>User ID: {userId}</p>
          ) : (
            <p>Product ID: {selectedNode}</p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
