import React, { useState } from "react";

function App() {
  const [userId, setUserId] = useState("");
  const [method, setMethod] = useState("SVD");
  const [recommendations, setRecommendations] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://localhost:5001/getRecommendations", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        userId,
        method,
      }),
    });

    if (response.ok) {
      const data = await response.json();
      setRecommendations(data);
    } else {
      console.error("Error fetching recommendations");
    }
  };

  return (
    <div className="App">
      <h1>Recommender System</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="userId">User ID: </label>
        <input
          type="text"
          id="userId"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
        />
        <br />

        <label htmlFor="method">Recommendation Method: </label>
        <select
          id="method"
          value={method}
          onChange={(e) => setMethod(e.target.value)}
        >
          <option value="SVD">SVD</option>
          <option value="SlopeOne">Slope One</option>
          <option value="Combined">Combined</option>
        </select>
        <br />

        <button type="submit">Get Recommendations</button>
      </form>

      <div>
        <h2>Recommendations:</h2>
        <ul>
          {recommendations.map((rec) => (
            <li key={rec.productId}>
              Product ID: {rec.productId} | Predicted Rating: {rec.predictedRating}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
