import React, { useState, useEffect, useCallback } from "react";
import Papa from "papaparse";
import GrapherWrapper from "./GrapherWrapper";
import "./App.css";

function App() {
  const [userId, setUserId] = useState("");
  const [graphData, setGraphData] = useState({ nodes: [], edges: [] });
  const [recommendations, setRecommendations] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    Papa.parse("/top10_recommendations_all_users_sample.csv", {
      download: true,
      header: true,
      dynamicTyping: true,
      complete: (results) => {
        setRecommendations(results.data);
        setIsLoading(false);
      },
      error: (err) => {
        console.error("Error loading recommendations:", err);
        setIsLoading(false);
      }
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!userId) return;

    const userRecs = recommendations
      .filter(r => r.user_id === userId)
      .sort((a, b) => b.predicted_rating - a.predicted_rating)
      .slice(0, 10);

    const nodes = [
      { id: `user-${userId}`, label: `User ${userId}`, level: 0 }
    ];

    const edges = [];

    userRecs.forEach((rec, idx) => {
      nodes.push({
        id: rec.product_id,
        label: `${rec.product_id}\nScore: ${rec.predicted_rating.toFixed(2)}`,
        level: 1
      });
      edges.push({
        source: `user-${userId}`,
        target: rec.product_id
      });
    });

    setGraphData({ nodes, edges });
  };

  return (
    <div className="app-container">
      <h1>Recommendation Explorer</h1>

      {isLoading ? (
        <div className="loading">Loading recommendations data...</div>
      ) : (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            placeholder="Enter User ID"
            required
          />
          <button type="submit">Show Recommendations</button>
        </form>
      )}

      <GrapherWrapper
        nodes={graphData.nodes}
        edges={graphData.edges}
      />
    </div>
  );
}

export default App;
