import React, { useEffect, useRef } from "react";
import Graph from "graphology";
import Sigma from "sigma";

function GrapherWrapper({ nodes, edges, onNodeClick }) {
  const containerRef = useRef(null);
  const sigmaInstanceRef = useRef(null);

  useEffect(() => {
    if (!containerRef.current) return;

    // Group nodes by level
    const nodesByLevel = {};
    nodes.forEach((node) => {
      const lvl = node.level || 0;
      if (!nodesByLevel[lvl]) nodesByLevel[lvl] = [];
      nodesByLevel[lvl].push(node);
    });

    // Sort levels (0, 1, 2, …)
    const levelKeys = Object.keys(nodesByLevel).sort((a, b) => a - b);

    // Define spacing (you can adjust these values)
    const verticalSpacing = 100; // vertical gap between levels
    const horizontalSpacing = 100; // horizontal gap between nodes

    // Calculate coordinates for each node
    const coords = {};
    levelKeys.forEach((lvl) => {
      const levelNodes = nodesByLevel[lvl];
      const count = levelNodes.length;
      // Compute starting x so that the nodes are centered
      const startX = -(count - 1) / 2 * horizontalSpacing;
      levelNodes.forEach((node, index) => {
        coords[node.id] = {
          x: startX + index * horizontalSpacing,
          y: -lvl * verticalSpacing, // level 0: y=0; level 1: y=-100; etc.
        };
      });
    });

    // Create a new graph
    const graph = new Graph();
    nodes.forEach((node) => {
      const { x, y } = coords[node.id];
      graph.addNode(node.id, {
        label: node.label,
        x,
        y,
        size: 8,
      });
    });

    // Add edges
    edges.forEach((edge, index) => {
      graph.addEdge(edge.source, edge.target, {
        label: `Edge ${index}`,
      });
    });

    // Initialize Sigma with the precomputed layout
    const renderer = new Sigma(graph, containerRef.current);
    sigmaInstanceRef.current = renderer;

    // Listen for node clicks
    renderer.on("clickNode", (e) => {
      if (onNodeClick) onNodeClick(e.node);
    });

    // Cleanup on unmount or update
    return () => {
      renderer.kill();
      sigmaInstanceRef.current = null;
    };
  }, [nodes, edges, onNodeClick]);

  return (
    <div
      ref={containerRef}
      style={{
        width: "100%",
        height: "500px",
        border: "1px solid #ccc",
        marginTop: "1rem",
      }}
    />
  );
}

export default GrapherWrapper;
